INSERT INTO `modx_site_content` VALUES ('12','document','text/html','Mechanical Engineering','Mechanical Engineering','','mechanicalengineering','','1','0','0','5','0','','<h3>Professors:</h3>\n<p><strong>AZUMA, Takashi, Dr. Eng.</strong><br />Email: azuma[at]fel.t.u-tokyo.ac.jp<br />Medical Imaging, Ultrasound Imaging, Therapeutic Ultrasound</p>\n<p><strong>DAIGUJI, Hirofumi, Dr. Eng.</strong><br /> Email: daiguji[at]thml.t.u-tokyo.ac.jp<br /> Thermal Engineering, Nanoscale Transport Phenomena, Energy Systems</p>\n<p><strong>IZUMI, Satoshi, Dr. Eng.</strong><br /> Email: izumi[at]fml.t.u-tokyo.ac.jp<br /> Computational Machanics, Molecular Dynamics</p>\n<!--<p><strong>KITAJIMA, Muneo, Dr. Eng.<br /></strong>Email: mkitajima[at]kjs.nagaokaut.ac.jp<br />Human-Centered Design, Cognitive Science, Human-Computer Interaction</p>-->\n<p><strong>MARUYAMA, Shigeo, Dr. Eng.</strong><br /> Email: maruyama[at]photon.t.u-tokyo.ac.jp<br /> Carbon Nanotubes, Graphene, Solar Cells, Nano-Thermal Engineering, Molecular Dynamic</p>\n<p><strong>MITSUISHI, Mamoru, Dr. Eng.</strong><br /> Email: mamoru[at]nml.t.u-tokyo.ac.jp<br /> Intelligent Manufacturing, Computer-Integrated Medical Robotics</p>\n<p><strong>MURAKAMI, Tamotsu, Dr. Eng.</strong><br /> Email: murakami[at]mech.t.u-tokyo.ac.jp<br /> Design Engineering, Information Technology for Synthesis, Solid Freedom Fabrication</p>\n<p><strong>NAKAO, Masayuki, Dr. Eng.</strong><br /> Email: nakao[at]hnl.t.u-tokyo.ac.jp<br /> Micro-manufacturing, Machining Tool Instrumentation<br /><br /><strong>SHIOMI, Junichiro, Ph. D.</strong><br />Email: shiomi[at]photon.t.u-tokyo.ac.jp<br />Nano-scale Thermal Energy Engineering, Interfacial fluid dynamics, Thermoelectrics</p>\n<p><strong>SUGITA, Naohiko, Dr. Eng.</strong><br /> Email: sugi[at]mfg.t.u-tokyo.ac.jp<br /> Manufacturing, Cutting of Hard-Brittle Materials, Medical Device</p>\n<p><strong>SUZUKI, Yuji, Dr. Eng.</strong><br /> Email: ysuzuki[at]mesl.t.u-tokyo.ac.jp<br /> Micro energy, Flame