INSERT INTO `modx_site_htmlsnippets` VALUES ('11','CF-config','','1','0','0','0','7','0','#\n# cfFormMailer for MODx configuration v1.3\n#\n# #(半角シャープ）で始まる行はコメントです\n\n#------------------------------------------------------\n# 管理者宛メール設定\n#------------------------------------------------------\n# 管理者メール送信先（コンマ区切りで複数指定可）\n#    （省略した場合は「送信者メールアドレス」になります）\nadmin_mail = example@example.com\n\n# 送信者名\nadmin_name =\n\n# 管理者宛メール件名\nadmin_subject = メールフォームからのメールです\n\n# HTMLメールフラグ（0: テキストメール / 1: HTMLメール）\nadmin_ishtml = 0\n\n# CC 送信先\n# 管理者宛メールをCCで送信したい宛先がある場合は、そのアドレスをコンマ区切りで指定\nadmin_mail_cc =\n\n# BCC 送信先\n# 管理者宛メールをBCCで送信したい宛先がある場合は、そのアドレスをコンマ区切りで指定\nadmin_mail_bcc =\n\n# 送信先動的変更\n# 選択肢によって送信先を変えるselectフィールド名またはradioフィールド名を指定\ndynamic_send_to_field =\n\n\n#------------------------------------------------------\n# 自動返信メール設定\n#------------------------------------------------------\n\n# 自動返信メールフラグ (0: 自動返信しない / 1: 自動返信する）\nauto_reply = 1\n\n# 自動返信メールの宛先として使用するフィールド名\n#  (省略の場合は email )\n#  ※1つのフィールド名、または複数のフィールド名を + 記号で繋いで指定。アットマークはそのまま記述可。[例] email1+@+email2\nreply_to = email\n\n# 自動返信メールの送信者アドレス\n#  (省略の場合は管理者メール送信先[複数の場合は先頭のアドレス]）\nreply_from = \n\n# 自動返信メールの送信者名\n#  (省略の場合はサイト名）\nreply_fromname = \n\n# 自動返信メール件名\nreply_subject = お問い合わせありがとうございます\n\n# HTMLメールフラグ（0: テキストメール / 1: HTMLメール）\nreply_ishtml = 0\n\n# 添付するファイル\n# フォーム送信者に送信するファイルを絶対パスで記述 （例： /var/www/path/to/file/sample.pdf )\n# ※ファイル名は半角英数字で指定してください\n# ※半角コンマ区切りで複数のファイルを指定することができます\nattach_file = \n\n# 添付するファイルの名称\n# 前項「添付するファイル」を送信する際に付けるファイル名。省略の場合は「添付するファイル」のファイル名と同様。\nattach_file_name =\n\n#------------------------------------------------------\n# テンプレート (チャンク名またはリソースIDで指定）\n#------------------------------------------------------\n\n# 入力画面テンプレート\ntmpl_input = tmpl_input\n\n# 確認画面テンプレート\ntmpl_conf = tmpl_conf\n\n# 完了画面テンプレート\ntmpl_comp = tmpl_comp\n\n# 管理者宛メールテンプレート\ntmpl_mail_admin = tmpl_mail_admin\n\n# 自動返信メールテンプレート\ntmpl_mail_reply = tmpl_mail_reply\n\n# 自動返信メールテンプレート（モバイル用）\n# PCメール宛と携帯メール宛でテンプレートを変えたい場合に指定します\ntmpl_mail_reply_mobile =\n\n# 送信後遷移する完了画面リソースIDまたはURL\n# 送信完了と同時に完了画面（入力・確認画面とは違うURL）へ遷移するようにします。「tmpl_comp」の指定より優先されます。\ncomplete_redirect =\n\n#------------------------------------------------------\n# その他の設定\n#------------------------------------------------------\n\n# CAPTCHA による認証 （1: 使用する / 0:使用しない[デフォルト]）\n#   ※使用する場合は入力画面テンプレート内に画像表示用タグと入力フィールドを用意してください。\n#     例： <p>認証コード： <img src=\"[+verimageurl+] /><input type=\"text\" name=\"vericode\" valid=\"1:vericode\" /></p>\nvericode = 0\n\n# エラーのあるフィールドに対して挿入されるクラスセレクタ名\n#   （省略の場合は付加しない）\ninvalid_class = invalid\n\n# HTML メール送信時の、フィールド内での HTML タグ使用可否 （0: 不可。HTML タグはエスケープされます / 1: 入力値をそのまま）\n#  （省略の場合は 0 。リスクを理解できる方のみ設定してください。）\nallow_html = 0\n\n# ファイルアップロード時に使用するディレクトリの設定（0: サーバデフォルトのテンポラリエリア[デフォルト] / 1: スニペットディレクトリ内のtmpディレクトリ）\n# XREAなど、SAFE Modeがonの環境でファイルアップロード送信がうまくいかない場合は1にしてください\nupload_tmp_path = 0\n\n# 改行スタイル\n# 送信するメールのヘッダに使われる改行コード。通常は変更しなくても大丈夫です。(0: \\n (linuxスタイル)[デフォルト] / 1: \\r\\n (windowsスタイル))\nlf_style = 0\n\n# 送信内容をデータベースに記録する （0: 記録しない[デフォルト] / 1: 記録する）\n# この機能を使用するには、あらかじめcfFormDBモジュールをインストールし、記録用テーブルを作成しておく必要があります\nuse_store_db = 1\n\n# メール送信フラグ　（0: 送信しない / 1: 送信する[デフォルト]）\n# 0を指定すると管理者宛、自動返信ともにメール送信しなくなります。\nsend_mail = 1\n\n# 送信メール文字コード\n# （省略の場合は iso-2022-jp）\nmail_charset = \n','0');
INSERT INTO `modx_site_htmlsnippets` VALUES ('12','tmpl_mail_reply','','1','0','0','0','7','0','<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\">\n<html>\n<head>\n<style type=\"text/css\">\nth { vertical-align: top; text-align: left; color: #1E6C8A; padding-right: 1em;}\nfieldset { margin-bottom: 20px;}\nlegend { color: #1E6C8A;}\n.note { color:#666;}\n</style>\n</head>\n<body>\n<p class=\"note\">---------------------------------------------------</br />\n　これは自動返信メールです<br />\n---------------------------------------------------</p>\n<u><strong>[+name_f+] [+name_l+]</strong> 様</u>\n<p>お問い合わせありがとうございました。<br />\n以下の内容で受け付けましたのでご確認ください。</p>\n<p>送信日時： [+senddate|dateformat(%Y年%m月%d日 %H時%M分)+]</p>\n\n\n  <fieldset><legend>個人情報</legend>\n  <table>\n    <tbody>\n      <tr>\n        <th>お名前</th>\n        <td>[+name_f+] [+name_l+]</td>\n      </tr>\n      <tr>\n        <th>会社名</th>\n        <td>[+company+]</td>\n      </tr>\n      <tr>\n        <th>メールアドレス</th>\n        <td>[+reply_to+]</td>\n      </tr>\n      <tr>\n        <th>性別</th>\n        <td>[+gender+]</td>\n      </tr>\n      <tr>\n        <th>郵便番号</th>\n        <td>[+zip1+] - [+zip2+]</td>\n      </tr>\n      <tr>\n        <th>住所</th>\n        <td>[+pref+] [+addr1+] [+addr2+] [+addr3+]</td>\n      </tr>\n      <tr>\n        <th>電話番号</th>\n        <td>[+tel+]</td>\n      </tr>\n      <tr>\n        <th>FAX番号</th>\n        <td>[+fax+]</td>\n      </tr>\n    </tbody>\n  </table>\n  </fieldset>\n  <fieldset><legend>お問い合わせ内容</legend>\n  <table>\n    <tbody>\n      <tr>\n        <th>お問い合わせ内容</th>\n        <td>[+summary+]</td>\n      </tr>\n      <tr>\n        <th>メッセージ</th>\n        <td>[+message+]</td>\n      </tr>\n      <tr>\n        <th>優先する連絡方法</th>\n        <td>[+way+]</td>\n      </tr>\n    </tbody>\n  </table>\n  </fieldset>\n  <fieldset><legend>アンケート</legend>\n  <table>\n    <tbody>\n      <tr>\n        <th>既にお持ちの商品</th>\n        <td><ul>[+own|implodetag(li)+]</ul></td>\n      </tr>\n      <tr>\n        <th>印象に残った商品</th>\n        <td><ul>[+impression|implodetag(li)+]</ul></td>\n      </tr>\n    </tbody>\n  </table>\n  </fieldset>\n\n<hr />\n\n<p>○×株式会社<br />\n□△課 ○○　○○</p>\n<address>Email: <a href=\"mailto:[+adminmail+]\">[+adminmail+]</a><br />\n<a href=\"http://example.com/\">http://example.com</a></address>\n</body>\n</html>\n','0');
INSERT INTO `modx_site_htmlsnippets` VALUES ('13','tmpl_mail_admin','','1','0','0','0','7','0','---------------------------------------\nメールフォームから送信されたメールです\n---------------------------------------\n送信日時： [+senddate|dateformat(%Y年%m月%d日 %H時%M分)+]\n\n【個人情報】\nお名前:  [+name_f+] [+name_l+]\n会社名:  [+company+]\nメールアドレス:[+reply_to+]\n性別:    [+gender+]\n郵便番号:[+zip1+] - [+zip2+]\n住所:    [+pref+] [+addr1+] [+addr2+] [+addr3+]\n電話番号:[+tel+]\nFAX番号: [+fax+]\n\n【お問い合わせ内容】\nお問い合わせ内容: [+summary+]\nメッセージ:\n[+message+]\n\n優先する連絡方法: [+way+]\n\n【アンケート】\n既にお持ちの商品:\n[+own|implode(  /  )+]\n\n印象に残った商品:\n[+impression|implode(  /  )+]\n\n\n---------------------------------------\n[IPアドレス] [+sender_ip+]\n[ホスト名] [+sender_host+]\n[エージェント] [+sender_ua+]\n','0');
INSERT INTO `modx_site_htmlsnippets` VALUES ('14','tmpl_input','','1','0','0','0','7','0','<iferror><p class=\"error\"><strong>入力に誤りがあります。</strong><br />訂正して再送信してください。</p></iferror>\n<form action=\"[~[*id*]~]\" method=\"post\" id=\"cf_form\">\n  <fieldset>\n    <table>\n    <tbody>\n    \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td width=\"260\" style=\"background-color:#CCF\"><label for=\"f_name\">Name</label></td>\n        <td>Last/Family Name <input type=\"text\" name=\"name_l\" size=\"12\" id=\"f_name\" valid=\"1\" /> <br />First Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type=\"text\" name=\"name_f\" size=\"12\" /><br />Middle Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type=\"text\" name=\"name_m\" size=\"12\"/></td>\n      </tr>\n      \n                       <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_birthday\">Birthday</label></td>\n        <td>year<input type=\"text\" name=\"birthday_y\" size=\"4\" /> month <input type=\"text\" name=\"birthday_m\" size=\"2\" />day <input type=\"text\" name=\"birthday_d\" size=\"2\" /></td>\n      </tr>\n      \n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_age\">Age<br />(as of April 1st, 2014)</label></td>\n        <td><input type=\"text\" name=\"age\" id=\"f_age\" size=\"4\" /></td>\n      </tr>\n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\">Sex</td>\n        <td>\n          <input type=\"radio\" name=\"gender\" id=\"f_gender1\" value=\"male\" valid=\"1::性別\" /><label for=\"f_gender1\">male</label>\n          <input type=\"radio\" name=\"gender\" id=\"f_gender2\" value=\"female\" /><label for=\"f_gender2\">female</label></td>\n      </tr>\n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_nationality\">Nationality<br />(If you have more than one nationality, please describe them specifically.)</label></td>\n        \n        <td><textarea name=\"nationality_1\" rows=\"6\" cols=\"13\"></textarea>\n        <textarea name=\"nationality_2\" rows=\"6\" cols=\"13\"></textarea>\n        <textarea name=\"nationality_3\" rows=\"6\" cols=\"13\"></textarea></td>\n      </tr>\n      \n      \n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_email\">E-mail address</label></td>\n        <td><input type=\"text\" name=\"email\" size=\"40\" id=\"f_email\" />\n        </td>\n      </tr>\n\n\n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_department\">Department you are going to apply for</label></td>\n        \n        <td><textarea name=\"department\" rows=\"6\" cols=\"50\"></textarea></td>\n      </tr>\n      \n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_course\">Course you are going to apply for (Master\'s course or Doctoral course</label></td>\n        \n        <td><textarea name=\"course\" rows=\"6\" cols=\"50\"></textarea></td>\n      </tr>\n      \n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_form001\">The University you graduated from, or you will graduate from</label></td>\n        \n        <td><textarea name=\"form001\" rows=\"6\" cols=\"50\"></textarea></td>\n      </tr>\n      \n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td style=\"background-color:#CCF\"><label for=\"f_form002\">The prospective supervisor:the name of the professor you are going to ask to write an acceptance letter</label></td>\n        \n        <td><textarea name=\"form002\" rows=\"6\" cols=\"50\"></textarea></td>\n      </tr>\n\n\n<tr>\n<td colspan=\"2\">※If your name is shown differently in your passport, please write it below as it is shown<br /></td>\n</tr>\n\n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td width=\"260\" style=\"background-color:#CCF\"><label for=\"f_name\">Your name shown in your passport</label></td>\n        <td>Last/Family Name <input type=\"text\" name=\"passportname_l\" size=\"12\" id=\"f_passportname\" valid=\"1\" /> <br />First Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type=\"text\" name=\"passportname_f\" size=\"12\" /><br />Middle Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type=\"text\" name=\"passportname_m\" size=\"12\"/></td>\n      </tr>\n      \n                 <tr style=\"border-bottom:#666 solid 1px\">\n        <td width=\"260\" style=\"background-color:#CCF\"><label for=\"f_chinesename\">Your name Chinese Characters(If you use Chinese Characters in your name)</label></td>\n        <td>Last/Family Name <input type=\"text\" name=\"chinesename_l\" size=\"12\" id=\"f_chinesename\" valid=\"1\" /> <br />First Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type=\"text\" name=\"chinesename_f\" size=\"12\" /></td>\n      </tr>\n      \n\n    </tbody>\n  </table>\n  </fieldset>\n  \n  <p class=\"buttons\"><input type=\"submit\" value=\"　Confirmation　\" /> <input type=\"reset\" value=\"Reset\" /></p>\n</form>','0');
